mapper.select:
select user_nick, user_id, user_name, tel, email, date_format(register_date,'%Y.%m.%d') as register_date, permission, want, career_year, career from user
permission=#{param1}
AND want='T'
and user_nick like '%${param3.searchWord}%' or user_id like '%${param3.searchWord}%' or user_name like '%${param3.searchWord}%' or tel like '%${param3.searchWord}%' or email like '%${param3.searchWord}%' or register_date like '%${param3.searchWord}%' or career_year like '%${param3.searchWord}%' or career like '%${param3.searchWord}%'
order by user_id limit ${param3.onePageRecord} offset ${param3.offsetIndex}

select count(user_id) from user


    permission=#{param1}

   AND want='T'

select board.board_num,report.report_num,board.title,board.user_id as writer,report.user_id,report.report_content,date_format(report.report_time,'%Y.%m.%d') AS report_time, report.report_type_num,report.confirm from report INNER JOIN board ON report.board_num = board.board_num
confirm=#{param1}
and board.board_num like '%${param2.searchWord}%' or report.user_id like '%${param2.searchWord}%' or report.report_content like '%${param2.searchWord}%' or report.report_time like '%${param2.searchWord}%' or report.report_type_num like '%${param2.searchWord}%' or report.confirm like '%${param2.searchWord}%'
order by report.report_num limit ${param2.onePageRecord} offset ${param2.offsetIndex}

SELECT count(confirm) FROM report
confirm=#{param1}
and board_num like '%${param2.searchWord}%' or user_id like '%${param2.searchWord}%' or report_content like '%${param2.searchWord}%' or report_time like '%${param2.searchWord}%' or report_type_num like '%${param2.searchWord}%' or confirm like '%${param2.searchWord}%'
